<script lang="ts" setup>
import { ref } from 'vue'
import { Icon } from 'tdesign-icons-vue-next'
import './assets/css/style.css'

const menuValue = ref('item1')

const changeHandler = (active:any) => {
  console.log('change', active)
}

function jump(url: string){
  window.location.href = url
}

function scrollTo(item:number,y:number){
  window.removeEventListener('scroll', scrollListener)
  menuValue.value = 'item' + item
  window.scrollTo({
    top: y,
    behavior: 'smooth'
  })
  setTimeout(()=>window.addEventListener('scroll', scrollListener), 3000)
}

function scrollListener(){
  if ((document.documentElement.scrollTop || document.body.scrollTop) < 500){
    menuValue.value = 'item1'
  }else if((document.documentElement.scrollTop || document.body.scrollTop) < 1000){
    menuValue.value = 'item2'
  }else{
    menuValue.value = 'item3'
  }
}

window.addEventListener('scroll', scrollListener)

</script>